// import React from "react";
import { useState } from 'react';
import ReactDOM from 'react-dom/client';
import style from './style.module.scss';

const ContactUs = () => {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [message, setMessage] = useState("");
    const[list,setList] = useState([]);


  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(name,email,message)
    const date={name,email,message}
    if(name&&email&&message){
        setList((ls)=>[...ls,date])
        setName("")
        setEmail("")
        setMessage("")
    }
  }

    return(
        <div>
            <div className={style.contact}>
                <h1><span>We appreciate any feedback. Please share your thoughts with us! </span></h1>
                <form onSubmit={handleSubmit}>
      
        <input type="text" value={name} placeholder="Your Name" onChange={(e) => setName(e.target.value)} />
        <input type="text" value={email} placeholder="Your Email" onChange={(e) => setEmail(e.target.value)} />
        <input type="text" value={message} placeholder="Your Name" onChange={(e) => setMessage(e.target.value)} />
      <input type="submit" />
    </form>
            </div>
        </div>
    );
}
export default ContactUs;
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<ContactUs />);




